package com.matao.adapter;import java.util.ArrayList;import java.util.List;import android.annotation.SuppressLint;import android.content.Context;import android.view.View;import android.view.ViewGroup;import android.widget.BaseAdapter;import android.widget.ImageView;import android.widget.RelativeLayout;import com.matao.matao.R;import com.matao.utils.MTUtils;/** * @author: ZhouYang * @E-mail: ZhouYangGaoGao@163.com * @time:2015-5-6 下午4:46:35 * @Description:表情图片适配器 */public class BQGridAdapter extends BaseAdapter {	private Context context;	private List<Integer> list;	public BQGridAdapter(Context context, int pagerIndex) {		super();		this.context = context;		list = new ArrayList<Integer>();		if (pagerIndex == 4) {			for (int i = pagerIndex * 21; i < ids.length; i++) {				list.add(ids[i]);			}		} else {			for (int i = pagerIndex * 21; i < (pagerIndex + 1) * 21; i++) {				list.add(ids[i]);			}		}	}	@Override	public int getCount() {		return list.size();	}	@Override	public Object getItem(int position) {		return list.get(position);	}	@Override	public long getItemId(int position) {		return position;	}	@SuppressLint("ViewHolder")	@Override	public View getView(int position, View cv, ViewGroup parent) {		cv = View.inflate(context, R.layout.bq_img, null);		ImageView img = (ImageView) cv				.findViewById(R.id.reply_item_gridview_img);		RelativeLayout.LayoutParams lp = (android.widget.RelativeLayout.LayoutParams) img				.getLayoutParams();		lp.height = (int) ((MTUtils.getScreenWidth(context) - 80) * 0.14286);		lp.width = lp.height;		img.setLayoutParams(lp);		img.setImageResource(list.get(position));		return cv;	}	private int[] ids = { R.drawable.p0, R.drawable.p1, R.drawable.p2,			R.drawable.p3, R.drawable.p4, R.drawable.p5, R.drawable.p6,			R.drawable.p7, R.drawable.p8, R.drawable.p9, R.drawable.p10,			R.drawable.p11, R.drawable.p12, R.drawable.p13, R.drawable.p14,			R.drawable.p15, R.drawable.p16, R.drawable.p17, R.drawable.p18,			R.drawable.p19, R.drawable.delete, R.drawable.p20, R.drawable.p21,			R.drawable.p22, R.drawable.p23, R.drawable.p24, R.drawable.p25,			R.drawable.p26, R.drawable.p27, R.drawable.p28, R.drawable.p29,			R.drawable.p30, R.drawable.p31, R.drawable.p32, R.drawable.p33,			R.drawable.p34, R.drawable.p35, R.drawable.p36, R.drawable.p37,			R.drawable.p38, R.drawable.p39, R.drawable.delete, R.drawable.p40,			R.drawable.p41, R.drawable.p42, R.drawable.p43, R.drawable.p44,			R.drawable.p45, R.drawable.p46, R.drawable.p47, R.drawable.p48,			R.drawable.p49, R.drawable.p50, R.drawable.p51, R.drawable.p52,			R.drawable.p53, R.drawable.p54, R.drawable.p55, R.drawable.p56,			R.drawable.p57, R.drawable.p58, R.drawable.p59, R.drawable.delete,			R.drawable.p60, R.drawable.p61, R.drawable.p62, R.drawable.p63,			R.drawable.p64, R.drawable.p65, R.drawable.p66, R.drawable.p67,			R.drawable.p68, R.drawable.p69, R.drawable.p70, R.drawable.p71,			R.drawable.p72, R.drawable.p73, R.drawable.p74, R.drawable.p75,			R.drawable.p76, R.drawable.p77, R.drawable.p78, R.drawable.p79,			R.drawable.delete, R.drawable.p80, R.drawable.p81, R.drawable.p82,			R.drawable.p83, R.drawable.p84, R.drawable.p85, R.drawable.p86,			R.drawable.p87, R.drawable.p88, R.drawable.p89, R.drawable.delete };}