package com.matao.adapter;import android.annotation.SuppressLint;import android.content.Context;import android.view.View;import android.view.ViewGroup;import android.widget.BaseAdapter;import android.widget.RelativeLayout;import com.matao.matao.R;import com.matao.utils.MTApplication;import com.matao.utils.MTUtils;import com.matao.view.RoundAngleImageView;/** * @author: ZhouYang * @E-mail: ZhouYangGaoGao@163.com * @time:2015-5-6 下午4:46:35 * @Description:评论列表中item图片适配器 */public class ReplyGridAdapter extends BaseAdapter {	private Context context;	private String[] list;	public ReplyGridAdapter(Context context, String[] list) {		super();		this.context = context;		this.list = list;	}	@Override	public int getCount() {		return list.length;	}	@Override	public Object getItem(int position) {		return list[position];	}	@Override	public long getItemId(int position) {		return position;	}	@SuppressLint("ViewHolder")	@Override	public View getView(int position, View cv, ViewGroup parent) {		cv = View.inflate(context, R.layout.gridview_img_layout, null);		RoundAngleImageView img = (RoundAngleImageView) cv				.findViewById(R.id.experience_item_gridview_img);		RelativeLayout.LayoutParams lp = (android.widget.RelativeLayout.LayoutParams) img				.getLayoutParams();		lp.height = (int) ((MTUtils.getScreenWidth(context) - 60) * 0.338);		lp.width = lp.height;		img.setLayoutParams(lp);		MTApplication.bmu.display(img, list[position],				MTUtils.getConfig(context, R.drawable.matao_220));		return cv;	}}