package com.matao.bean;import java.io.Serializable;/** * @author: ZhouYang * @E-mail: ZhouYangGaoGao@163.com * @time:2015-5-5 下午4:55:43 * @Description:TODO */public class LoginUserInfo implements Serializable {	private int UserId;	private String NickName;	private String Avatar;	private int UnReadMessageCount;	private int getUserId() {		return UserId;	}	public void setUserId(int userId) {		UserId = userId;	}	public String getNickName() {		return NickName;	}	public void setNickName(String nickName) {		NickName = nickName;	}	public String getAvatar() {		return Avatar;	}	public void setAvatar(String avatar) {		Avatar = avatar;	}	public int getUnReadMessageCount() {		return UnReadMessageCount;	}	public void setUnReadMessageCount(int unReadMessageCount) {		UnReadMessageCount = unReadMessageCount;	}	@Override	public String toString() {		return "LoginUserInfo [UserId=" + UserId + ", NickName=" + NickName				+ ", Avatar=" + Avatar + ", UnReadMessageCount="				+ UnReadMessageCount + "]";	}}